mapper.update:
update groups
set categoryno = #{categoryno}
where groupno = #{groupno}

update groups
set groupname = #{groupname}
where groupno = #{groupno} and categoryno = #{categoryno}

update groupboard
set boardcontents = #{boardcontents}
where bno = #{bno}

mapper.select:
  		
  		select *
		from memberinfo
		where mi_assembleName = 'withh';

  		select *
		from groupchat
		where memberno = #{memberno};
